  .text1 h2, .text2 h2 {
    font-size: 50px; /* Reduce text size */
  }

  .car-image {
    height: 200px; /* Adjust image height */
  }
}
/* Media query for tablets and larger screens (min-width: 768px) */
@media (min-width: 768px) {
  .signup-section {
    flex-direction: row; /* Horizontal layout for larger screens */
    justify-content: center; /* Center elements horizontally */
  }

  .signup-header {
    text-align: left; /* Align text to the left */
    margin-right: 10px; /* Space between header and subheader */
  }

  .signup-subheader {
    padding-left: 15px; /* Extra padding for larger screens */
    text-align: left; /* Align text to the left */
  }

  .email-input {
    width: 30%; /* Narrower input field for larger screens */
  }

  .signup-button {
    padding: 10px 30px; /* Larger padding for prominence */
  }
}

/* Media query for mobile devices (max-width: 480px) */
@media (max-width: 480px) {
  .signup-header {
    font-size: 1.5em; /* Smaller font size for mobile */
    margin-bottom: 5px; /* Reduce margin */
  }

  .signup-subheader {
    font-size: 1em; /* Smaller font size for mobile */
    border-left: none; /* No separator on mobile */
    padding-left: 5px; /* Less padding */
    text-align: center; /* Center-align text */
  }

  .email-input {
    width: 90%; /* Wider input field on mobile */
    padding: 10px; /* Less padding for smaller screens */
  }

  .signup-button {
    padding: 10px; /* Less padding for smaller screens */
    width: 100%; /* Button takes full width on mobile */
  }
}
